from logging import getLogger
from os import environ

### === pkg_resources ãŒ import ã•ã‚Œã‚‹ã“ã¨ã«ã‚ˆã‚Šã€
### === pkg_resources å†…ã® @call_aside é–¢æ•° ( _initialize, _initialize_master_working_set ) ãŒå®Ÿè¡Œã•ã‚Œã‚‹
from pkg_resources import iter_entry_points

from opentelemetry.instrumentation.dependencies import (
    get_dist_dependency_conflicts,
)
from opentelemetry.instrumentation.distro import BaseDistro, DefaultDistro
from opentelemetry.instrumentation.environment_variables import (
    OTEL_PYTHON_CONFIGURATOR,
    OTEL_PYTHON_DISABLED_INSTRUMENTATIONS,
    OTEL_PYTHON_DISTRO,
)
from opentelemetry.instrumentation.version import __version__

_logger = getLogger(__name__)

def _load_distro() -> BaseDistro:
    distro_name = environ.get(OTEL_PYTHON_DISTRO, None)
    # print("distro_name", distro_name) # None
    # print("iter_entry_points(otel_distr)", iter_entry_points("opentelemetry_distro"))
    for entry_point in iter_entry_points("opentelemetry_distro"):
        print("=== load_distro ã® entry_point", entry_point) # distro = opentelemetry.distro:OpenTelemetryDistro
        try:
            # If no distro is specified, use first to come up.
            if distro_name is None or distro_name == entry_point.name:
                # ã“ã£ã¡ã«å…¥ã‚‹ã€‚ã¾ãŸä¾å­˜é–¢ä¿‚ã®è§£æ±ºçš„ãªã“ã¨ã‚’ã—ã¦ã„ã‚‹
                distro = entry_point.load()()
                if not isinstance(distro, BaseDistro):
                    _logger.debug(
                        "%s is not an OpenTelemetry Distro. Skipping",
                        entry_point.name,
                    )
                    continue
                _logger.debug(
                    "Distribution %s will be configured", entry_point.name
                )
                print(f"ğŸ‰ğŸ‰ğŸ‰ Distribution {entry_point} will be configured")
                return distro # opentelemetry.distro.OpenTelemetryDistro ãŒè¿”å´ã•ã‚Œã‚‹
        except Exception as exc:  # pylint: disable=broad-except
            _logger.exception(
                "Distribution %s configuration failed", entry_point.name
            )
            raise exc
    return DefaultDistro()


def _load_instrumentors(distro):
    package_to_exclude = environ.get(OTEL_PYTHON_DISABLED_INSTRUMENTATIONS, []) 
    # â†‘ ä½•ã‚‚è¨­å®šã—ãªã‘ã‚Œã° [] ãŒã‹ãˆã‚‹
    # instrument ã—ãŸããªã„ãƒ©ã‚¤ãƒ–ãƒ©ãƒªã®è¨­å®šã‚’èª­ã¿å–ã‚ã†ã¨ã—ã¦ã„ã‚‹å¤šåˆ†
    if isinstance(package_to_exclude, str):
        package_to_exclude = package_to_exclude.split(",")
        # to handle users entering "requests , flask" or "requests, flask" with spaces
        package_to_exclude = [x.strip() for x in package_to_exclude]

    # print("æ ¸å¿ƒã«è¿«ã£ã¦ããŸ")
    # print("ã“ã‚Œã¯??", iter_entry_points("opentelemetry_pre_instrument"))
    # ã“ã“ã«ã¯å…¥ã‚‰ãªã„
    for entry_point in iter_entry_points("opentelemetry_pre_instrument"):
        # print("ã“ã“ã¯?", entry_point)
        entry_point.load()()

   # print("ã“ã‚Œã¯??", iter_entry_points("opentelemetry_instrument"))
    for entry_point in iter_entry_points("opentelemetry_instrumentor"):
        # è‡ªå‹•è¨ˆè£…ã—ãŸããªã„ãƒ©ã‚¤ãƒ–ãƒ©ãƒªã®æŒ‡å®šãŒã‚ã£ãŸã‚‰çœãå‡¦ç†
        if entry_point.name in package_to_exclude:
            _logger.debug(
                "Instrumentation skipped for library %s", entry_point.name
            )
            continue

        try:
            conflict = get_dist_dependency_conflicts(entry_point.dist)
            # print("conflict?", conflict)
            if conflict:
                _logger.debug(
                    "Skipping instrumentation %s: %s",
                    entry_point.name,
                    conflict,
                )
                continue

            # tell instrumentation to not run dep checks again as we already did it above
            # BaseDistro Class ã® load_instrumentor ãƒ¡ã‚½ãƒƒãƒ‰ã‚’å„ã‚¨ãƒ³ãƒˆãƒªãƒ¼ãƒã‚¤ãƒ³ãƒˆã«å¯¾ã—ã¦å®Ÿè¡Œã™ã‚‹
            distro.load_instrumentor(entry_point, skip_dep_check=True)
            _logger.debug("Instrumented %s", entry_point.name)
        except Exception as exc:  # pylint: disable=broad-except
            _logger.exception("Instrumenting of %s failed", entry_point.name)
            raise exc

    for entry_point in iter_entry_points("opentelemetry_post_instrument"):
        # print("ã“ã£ã¡ã«ã¯å…¥ã‚‹ã‚“ã‘ï¼Ÿ") => å…¥ã‚‰ãªã•ãã†ã ã£ãŸ
        entry_point.load()()

def _load_configurators():
    configurator_name = environ.get(OTEL_PYTHON_CONFIGURATOR, None) # None
    # print("configurator_name", configurator_name)
    configured = None
    for entry_point in iter_entry_points("opentelemetry_configurator"):
        if configured is not None:
            # ã“ã“ã«ã¯å…¥ã‚‰ãªã„
            _logger.warning(
                "Configuration of %s not loaded, %s already loaded",
                entry_point.name,
                configured,
            )
            continue
        try:
            if (
                configurator_name is None
                or configurator_name == entry_point.name
            ):
                # ã“ã“ã«å…¥ã‚‹
                # ã¾ãŸä¾å­˜é–¢ä¿‚ã®è§£æ±ºã§ã™ã‹?
                entry_point.load()().configure(auto_instrumentation_version=__version__)  # type: ignore
                configured = entry_point.name
            else:
                _logger.warning(
                    "Configuration of %s not loaded because %s is set by %s",
                    entry_point.name,
                    configurator_name,
                    OTEL_PYTHON_CONFIGURATOR,
                )
        except Exception as exc:  # pylint: disable=broad-except
            _logger.exception("Configuration of %s failed", entry_point.name)
            raise exc
